import React, { Component } from "react";
import { Form } from "react-bootstrap";

class Appraisal extends Component {
   constructor() {
    super();
    this.state = {
        name: "React"
    };
    this.onChangeValue = this.onChangeValue.bind(this);
    this.formSubmit = this.formSubmit.bind(this);
   }

   onChangeValue(e) {
        this.setState({
            selectedOption: e.target.value
        })
   }

   formSubmit(e) {
    e.preventDefault();
    console.log(this.state.selectedOption)
   }

    render(){
        return (
        <div>
            <Form onSubmit={this.formSubmit}>
                <div>
                    <label htmlFor="name">Name:</label>
                    <input type="text" id="name"/>
                </div>
                <div>
                    <label htmlFor="emp_id">Employee ID:</label>
                    <input type="text" id="emo_id"/>
                </div>
                <div>
                    <label htmlFor="pos">Position:</label>
                    <input type="text" id="pos"/>
                </div>
                <div>
                    <label htmlFor="dept">Department:</label>
                    <input type="text" id="dept"/>
                </div>
                <div>
                    <label htmlFor="check">Fresher/BenchedPerson:</label>
                    <input type="radio" id="check" value="Yes" name="Yes" clicked={this.state.selectedOption === "Yes"} onChange={this.onChangeValue}/>Yes
                    <input type="radio" id="check" value="No" name="No" clicked={this.state.selectedOption === "No"} onChange={this.onChangeValue}/>No
                </div>
            </Form>
        </div>
    );}
}

export default Appraisal;
