import React, { Component } from "react";
import { Form } from "react-bootstrap";

class Appraisal extends Component {
  constructor() {
    super();
    this.state = {
      name: "React",
    };
    this.onChangeValue = this.onChangeValue.bind(this);
    this.formSubmit = this.formSubmit.bind(this);
  }

  onChangeValue(e) {
    this.setState({
      selectedOption: e.target.value,
    });
  }

  formSubmit(e) {
    e.preventDefault();
    console.log(this.state.selectedOption);
    this.setState({
      isSubmitted: true,
    });
  }

  render() {
    const { isSubmitted } = this.state;
    return (
      <div>
        <Form onSubmit={this.formSubmit}>
          <div>
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" disabled={isSubmitted} />
          </div>
          <div>
            <label htmlFor="emp_id">Employee ID:</label>
            <input type="text" id="emo_id" disabled={isSubmitted} />
          </div>
          <div>
            <label htmlFor="pos">Position:</label>
            <input type="text" id="pos" disabled={isSubmitted} />
          </div>
          <div>
            <label htmlFor="dept">Department:</label>
            <input type="text" id="dept" disabled={isSubmitted} />
          </div>
          <div>
            <label htmlFor="check">Fresher/BenchedPerson:</label>
            <input
              type="radio"
              id="check"
              value="Yes"
              name="Yes"
              clicked={this.state.selectedOption === "Yes"}
              onChange={this.onChangeValue}
              disabled={isSubmitted}
            />
            Yes
            <input
              type="radio"
              id="check"
              value="No"
              name="No"
              clicked={this.state.selectedOption === "No"}
              onChange={this.onChangeValue}
              disabled={isSubmitted}
            />
            No
          </div>
          <button type="submit">Submit</button>
        </Form>
        <br />
        <br />
        <h1>Appraisal form - self</h1>
        <Form>
            <div>
                <label htmlFor="pro">No of Projects:</label>
                <input />
            </div>
        </Form>
      </div>
    );
  }
}

export default Appraisal;
