import React, { useEffect, useState } from "react";
import { Form } from "react-bootstrap";

function Projects() {
  const [data, setData] = useState([]);
  const [desc, setDesc] = useState([])

  const link = "http://localhost:8081/getData/naveen@gmail.com";
  console.log(data, "data");
  const fetchProject = async () => {
    try {
      const res = await fetch(link);
      const d = await res.json();
      console.log(d);
      setData((prevState) => {
        const updatedData = prevState ? [...prevState] : prevState;
        updatedData.push(d[0]);
        console.log(updatedData, "Updated");
        return updatedData;
      });
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchProject();
  }, []);

  return (
    <div>
      <Form>
        {data &&
          data.map((project, index) => {
            return (
              <div key={index}>
                <div>
                  <label htmlFor={`projectname_${index}`}>Project Name</label>
                  <input
                    id={`projectname_${index}`}
                    type="text"
                    value={project?.projects[index]?.projectName}
                    readOnly
                  />
                </div>
                <div>
                  <label htmlFor={`projectdescription_${index}`}>
                    Project Description
                  </label>
                  <input
                    id={`projectdescription_${index}`}
                    type="text"
                    value={project.projectDescription} 
                    onChange={(e) => {setDesc[index](e.target.value);console.log(e);}}
                  />
                </div>
              </div>
            );
          })}
      </Form>
    </div>
  );
}

export default Projects;
