import React, { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import { CircularProgressbar } from "react-circular-progressbar";
import 'react-circular-progressbar/dist/styles.css';

function GoalSetting() {
  const [percentage, setPercentage] = useState(0);
  const [inputField, setinputField] = useState([]);
  var [statusUpdater, setStatusUpdater] = useState("");

  useEffect(() => {
    setTimeout(() => {
      if (percentage < 100) {
        setPercentage(percentage + 1);
      }
    }, 50);
  }, [percentage]);

  const addInput = () => {
    setinputField([
      ...inputField,
      {
        Project_Name: "",
        Progress: "",
        Status: "",
        Last_Updated: "",
      },
    ]);
  };

  const functionStatus = (e) => {
    if (e.target < 100) {
      setStatusUpdater = "In Progress";
    } else {
      setStatusUpdater = "Completed";
    }
  };

  return (
    <div>
      <header style={{display: "flex"}}>

        <button onClick={addInput}>Add+</button>
      </header>
      <Table>
        <thead>
          <tr>
            <th>Project Name</th>
            <th>Progress</th>
            <th>Status</th>
            <th>Last Update</th>
          </tr>
        </thead>
        <tbody>
          {inputField.map((field, index) => (
            <tr key={index}>
              <td>
                <input
                  type="text"
                  name={`Project_Name_${index}`}
                  placeholder="Enter the Project Name"
                />
              </td>
              <td>
                <input
                  type="range"
                  name={`Progress_${index}`}
                  min={0}
                  max={100}
                  onChange={(e) => functionStatus(e)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name={`Status_${index}`}
                  value={statusUpdater}
                  text={`${statusUpdater}`}
                />
              </td>
              <td>
                <input
                  type="date"
                  name={`Last_Update_${index}`}
                  max={new Date().toLocaleDateString()}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default GoalSetting;
