        }
    }

    return (
        <div>
            <Form>
                <div>
                    <label htmlFor="name">Name:</label>
                    <input type="text" id="name"/>
                </div>
                <div>
                    <label htmlFor="emp_id">Employee ID:</label>
                    <input type="text" id="emo_id"/>
                </div>
                <div>
                    <label htmlFor="pos">Position:</label>
                    <input type="text" id="pos"/>
                </div>
                <div>
                    <label htmlFor="dept">Department:</label>
                    <input type="text" id="dept"/>
                </div>
                <div>
                    <label htmlFor="check">Fresher/BenchedPerson:</label>
                    <input type="checkbox" id="check" value="Yes" onChange={handleCheck}/>
                    <input type="checkbox" id="check" value="No" onChange={handleCheck}/>
                </div>
            </Form>
        </div>
    );
}

export default Appraisal;
