import React, { useState } from "react";
import { Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

function Appraisal() {
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [selectedOption, setSelectedOption] = useState("");
  const [inputField, setinputField] = useState();
  const history = useNavigate();

  const onChangeValue = (e) => {
    setSelectedOption(e.target.value);
  };

  const formSubmit = (e) => {
    e.preventDefault();
    console.log(selectedOption);
    setIsSubmitted(true);
  };

  const addInput = () => {
    setinputField([...inputField, {
      contribution:'',
    }])
  };

  const removeInput = (index) => {
    const rows = [...inputField];
    rows.splice(index, 1);
    setinputField(rows);
  }

  const handleChange = (index, e) 

  return (
    <div>
      <Form onSubmit={formSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" disabled={isSubmitted} />
        </div>
        <div>
          <label htmlFor="emp_id">Employee ID:</label>
          <input type="text" id="emo_id" disabled={isSubmitted} />
        </div>
        <div>
          <label htmlFor="pos">Position:</label>
          <input type="text" id="pos" disabled={isSubmitted} />
        </div>
        <div>
          <label htmlFor="dept">Department:</label>
          <input type="text" id="dept" disabled={isSubmitted} />
        </div>
        <div>
          <label htmlFor="check">Fresher/BenchedPerson:</label>
          <input
            type="radio"
            id="check"
            value="Yes"
            name="Yes"
            checked={selectedOption === "Yes"}
            onChange={onChangeValue}
            disabled={isSubmitted}
          />
          Yes
          <input
            type="radio"
            id="check"
            value="No"
            name="No"
            checked={selectedOption === "No"}
            onChange={onChangeValue}
            disabled={isSubmitted}
          />
          No
        </div>
        <button type="submit">Submit</button>
      </Form>
      <br />
      <br />
      <h1>Appraisal form - self</h1>
      <Form>
        <div>
          <label htmlFor="pro">No of Projects:</label>
          <input id="pro" type="number" />
        </div>
        <div>
          <label htmlFor="pro-desc">Projects Description:</label>
          <textarea id="pro-desc" type="text" />
        </div>
        <div>
          <label htmlFor="role">Role:</label>
          <input id="role" type="text" />
        </div>
        <div>
          <label htmlFor="ach">Achievements:</label>
          <textarea id="ach" />
        </div>
        <div>
          <div>
            <label htmlFor="oc">Other Contributions:</label>
            <input
              id="oc"
              type="text"
              onChange={(e) => handleChange(index, e)}
              value={contribution}
              name="contribution"
            />
            <div>
              {(inputField.length !== 1) ? (
                <button onClick={removeInput}>Delete</button>
              ) : (
                ""
              )}
            </div>
          </div>
          <button type="button" onClick={addInput}>
            Add+
          </button>
        </div>
        <div>
          <label htmlFor="cert">No of Certifications:</label>
          <input id="cert" type="number" />
        </div>
        <div>
          <label htmlFor="desc">Description of Certifications:</label>
          <textarea id="desc" type="text" />
        </div>
        <div>
          <label htmlFor="skl">Skills:</label>
          <textarea id="skl" type="text" />
        </div>
        <div>
          <label htmlFor="rat">Self Rating:</label>
          <input type="radio" value={1} id="rat" name="1" />1
          <input type="radio" value={2} id="rat" name="2" />2
          <input type="radio" value={3} id="rat" name="3" />3
          <input type="radio" value={4} id="rat" name="4" />4
          <input type="radio" value={5} id="rat" name="5" />5
        </div>
        {/* <div>
                <label>Any Other</label>
            </div> */}
        <button type="submit">Submit</button>
      </Form>
      <button onClick={history("/manage")}>Manager Form </button>
    </div>
  );
}

export default Appraisal;
