import React, { useEffect, useState } from "react";
import { CardFooter, CardTitle, Table } from "react-bootstrap";
import { CircularProgressbar } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";
import { Card } from "react-bootstrap";

function GoalSetting() {
  const [percentage, setPercentage] = useState(0);
  const [inputField, setinputField] = useState([]);
  const [range, setRange] = useState([]);
  const [Goals, setGoals] = useState(0);
  const [no, setNo] = useState(0);
  const [inpro, setInpro] = useState(0);
  const [com, setCom] = useState(0);

  useEffect(() => {
    setTimeout(() => {
      if (percentage < 100) {
        setPercentage(percentage + 1);
      }
    }, 50);
  }, [percentage]);

  const addInput = () => {
    setinputField([
      ...inputField,
      {
        Project_Name: "",
        Progress: 0,
        Status: "No Progress",
        Last_Updated: "",
      },
    ]);

    setRange([...range, 0]);
    setGoals(Goals + 1);
  };

  const updateStatus = (index, newValue) => {
    const updatedInputField = [...inputField];


    if (newValue === 0) {
      updatedInputField[index].Status = "No progress";
    } else if (newValue < 100) {
      updatedInputField[index].Status = "In Progress";
    } else {
      updatedInputField[index].Status = "Completed";
    }

    setinputField(updatedInputField);

    updateCount(status, updatedInputField[index].Status);
  };

  const updateCount = (prevStatus, newStatus) => {
    switch (prevStatus) {
      case "No progress":
        setNo(no - 1);
        break;
      case "In Progress":
        setInpro(inpro - 1);
        break;
      case "Completed":
        setCom(com - 1);
        break;
      default:
        break;
    }

    switch (newStatus) {
      case "No progress":
        setNo(no + 1);
        break;
      case "In Progress":
        setInpro(inpro + 1);
        break;
      case "Completed":
        setCom(com + 1);
        break;
      default:
        break;
    }
  };

  return (
    <div>
      <header style={{ display: "flex", flexDirection: "row" }}>
        <div style={{ display: "flex", flexDirection: "column" }}>
          <div style={{ textAlign: "center" }}>
            <h4>Your Progress</h4>
            <div style={{ width: 100, marginLeft: 21, height: 100 }}>
              <CircularProgressbar value={percentage} text={`${percentage}%`} />
            </div>
          </div>
        </div>
        <div>
            <Card>
                <CardTitle>{Goals}</CardTitle>
                <CardFooter>No.of Goals</CardFooter>
            </Card>
            <Card>
                <CardTitle>{inpro}</CardTitle>
                <CardFooter>In Progress</CardFooter>
            </Card>
            <Card>
                <CardTitle>{com}</CardTitle>
                <CardFooter>Completed</CardFooter>
            </Card>
            <Card>
                <CardTitle>{no}</CardTitle>
                <CardFooter>Not yet Started</CardFooter>
            </Card>
        </div>
        <button onClick={addInput} className="goalAdd ">Add Goal+</button>
      </header>
      <Table>
        <thead>
          <tr className="thead-goal">
            <th>Goal</th>
            <th>Progress</th>
            <th>Status</th>
            <th>Last Update</th>
          </tr>
        </thead>
        <tbody>
          {inputField.map((field, index) => (
            <tr key={index} className="thead-goal">
              <td>
                <input
                  type="text"
                  name={`Project_Name_${index}`}
                  placeholder="Set the Goal"
                  value={field.Project_Name}
                  onChange={(e) => {
                    const updatedInputField = [...inputField];
                    updatedInputField[index].Project_Name = e.target.value;
                    setinputField(updatedInputField);
                  }}
                />
              </td>
              <td>
                <input
                  type="range"
                  name={`Progress_${index}`}
                  min={0}
                  max={100}
                  onChange={(e) => updateStatus(index, parseInt(e.target.value))}
                  value={field.Progress}
                />{field.Progress}
              </td>
              <td>
                {field.Status}
              </td>
              <td>
                <input
                  type="date"
                  name={`Last_Update_${index}`}
                  max={new Date().toISOString().split('T')[0]}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default GoalSetting;
