import React, { useState } from "react";
import { Form } from "react-bootstrap";
import "./Appraisal.css";

function Appraisal() {
  const [contribution, setContribution] = useState("");
  const [communication, setCommunication] = useState("");
  const [initiatives, setInitiatives] = useState("");
  const [teamwork, setTeamwork] = useState("");
  const [potential, setPotential] = useState("");
  const [technicalexpertise, setTechnicalExpertise] = useState("");
  const [showTechnicalShowcase, setShowTechnicalShowcase] = useState(false);

  // const toggleTechnicalShowcase = () => {
  //   setShowTechnicalShowcase(!showTechnicalShowcase);
  // };

  return (
    <div>
      <Form className="appraisal form">
        <div>
          <label htmlFor="contribution">Contribution</label>
          <textarea
            id="contribution"
            rows={5}
            cols={5}
            onChange={(e) => setContribution(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="teamwork">Teamwork</label>
          <textarea
            id="teamwork"
            rows={5}
            cols={5}
            onChange={(e) => setTeamwork(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="technicalexpertise">Technical Expertise</label>
          <textarea
            id="technicalexpertise"
            rows={5}
            cols={5}
            onChange={(e) => setTechnicalExpertise(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="initiatives">Initiatives</label>
          <textarea
            id="initiatives"
            rows={5}
            cols={5}
            onChange={(e) => setInitiatives(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="communication">Communication</label>
          <textarea
            id="communication"
            rows={5}
            cols={5}
            onChange={(e) => setCommunication(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="potential">Potential</label>
          <textarea
            id="potential"
            rows={5}
            cols={5}
            onChange={(e) => setPotential(e.target.value)}
          />
        </div>
      </Form>
    </div>
  );
}

export default Appraisal;
